import React, { useState, useEffect } from "react";
import "./styles.css";

document.body.style.margin = "0";
document.body.style.padding = "0";
document.body.style.overflowX = "hidden"; // Prevent horizontal scrolling

const Home = ({ onStart }) => {
  const [showDetails, setShowDetails] = useState(false);

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setTimeout(() => setIsVisible(true), 300);
  }, []);

  const handleStart = () => {
    window.scrollTo(0, 0);
    onStart();
  };

  // Shared Bubble Style
  const bubbleStyle = {
    width: "1200px", // Fixed width for consistency
    margin: "20px auto", // Center horizontally
    borderRadius: "20px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    padding: "40px",
    textAlign: "center",
  };

  return (
    <div
      className={`fade-in ${isVisible ? "show" : ""}`}
      style={{
        backgroundColor: "white",
        fontFamily: "'Montserrat', sans-serif",
        color: "#333",
        lineHeight: "1.5",
      }}
    >
      {/* Top Bar */}
      <div
        style={{
          padding: "15px 0",
          fontFamily: "'Courier New', monospace",
          fontSize: "1.8rem",
          textAlign: "center",
          position: "sticky",
          top: 0,
          zIndex: 1000,
          width: "100%",
          backdropFilter: "blur(10px)",
          borderBottom: "2px solid rgba(0, 0, 0, 0.1)",
        }}
      >
        CPSC 183 Final Project
      </div>

      {/* Yellow Bubble */}
      <div
        style={{
          ...bubbleStyle,
          backgroundColor: "#FFF9C4",
        }}
      >
        <h1 style={{ fontSize: "2.5rem", margin: "0 0 20px" }}>
          A Personality Quiz on Copyright
        </h1>
        <p style={{ fontSize: "1.5rem", margin: "0", lineHeight: "1.8" }}>
          Below, you will find details on a fictional court case that falls into
          many legal and moral gray areas. We aim to create a “personality test”
          that will measure your legal philosophies to try and predict your
          stance on the given scenario.
        </p>
      </div>

      {/* Orange Bubble */}
      <div
        style={{
          ...bubbleStyle,
          backgroundColor: "#FFE082",
        }}
      >
        <h1 style={{ fontSize: "2.5rem", margin: "0 0 20px" }}>Summary</h1>
        <p style={{ fontSize: "1.5rem", margin: "0", lineHeight: "1.8" }}>
          A political advocacy group leverages an AI model trained on a dataset
          containing copyrighted books, movies, and social media posts to
          generate memes and short videos...
        </p>
      </div>

      {/* Dropdown Section */}
      <div
        style={{
          ...bubbleStyle,
          backgroundColor: "#FFF8E1",
          cursor: "pointer",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
        onClick={toggleDetails}
      >
        <span style={{ marginRight: "10px", fontSize: "2rem", fontWeight: "bold" }}>
          Full Details
        </span>
        <span
          style={{
            transform: showDetails ? "rotate(180deg)" : "rotate(0deg)",
            transition: "transform 0.3s",
            fontSize: "1.4rem",
          }}
        >
          ▼
        </span>
      </div>

      {/* Full Details Content */}
      {showDetails && (
        <div
          style={{
            ...bubbleStyle,
            backgroundColor: "#FFF3E0",
            textAlign: "left",
            lineHeight: "1.8",
          }}
        >
          <p>
            During a high-stakes election season, a political advocacy group
            called TrueVoice launched a digital campaign...
          </p>
        </div>
      )}

      {/* Continue Button */}
      <div style={{ textAlign: "center", margin: "40px auto" }}>
        <button
          onClick={handleStart}
          style={{
            padding: "15px 30px",
            fontSize: "1.8rem",
            backgroundColor: "#FFB300",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Continue
        </button>
      </div>
    </div>
  );
};

export default Home;
